                placeholderTextColor="gray"
                multiline
                numberOfLines={6}
                color='blue'
                fontSize={16}
                onChangeText={text => {

                }}
            />

            <Button
                style={{
                    backgroundColor: 'gray',
                    borderRadius: 10,
                    marginVertical: 8
                }}
                mode="Contained"
                onPress={() => {


                }}
            >
                <Text style={{ alignSelf: 'center', color: 'white' }}>ส่งคำตอบ</Text>
