import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, FlatList, TouchableOpacity } from 'react-native';
import { Button, Card } from 'react-native-paper';

import axiox from 'axios';
import { COLORS, SIZES } from '../constants/theme';

import moment from 'moment';
import 'moment/locale/th';
import { USER } from '../constants/variables';

const ManagerAssessmentScreen = ({ navigation, route }) => {

    // let sheetID = route.params.sheetID;
    let userId = route.params.userId;
    let year = route.params.year;
    let part = route.params.part;

    const [answer, setAnswer] = useState('');
    const [loading, setLoading] = useState(false);
    const [allAnswer, setAllAnswer] = useState([]);

    useEffect(() => {
        setLoading(true);
        // USER.position === "ผู้จัดการ" ? getAllAnswer() : getAnswer()

    }, []);

    const getAllAnswer = () => {
        setLoading(true);
        console.log(USER.department)
        axiox.get('http://localhost:8080/answer/manager_assessment/' + year + '/' + part + '/department/' + USER.department)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);
                setAllAnswer(res.data)
                // alert("บันทึกข้อมูลสำเร็จ");
                setLoading(false);
            })
            .catch(err => {
                console.log(err)
                // alert("บันทึกข้อมูลล้มเหลว กรุณาลองใหม่");
                setLoading(false);

            });
    }

    const getAnswer = () => {
        setLoading(true);
        axiox.get('http://localhost:8080/answer/manager_assessment/' + year + '/' + part + '/' + userId)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);
                setAnswer(res.data[0].answer)
                // alert("บันทึกข้อมูลสำเร็จ");
                setLoading(false);
            })
            .catch(err => {
                console.log(err)
                // alert("บันทึกข้อมูลล้มเหลว กรุณาลองใหม่");
                setLoading(false);

            });
    }


    const postAnswer = ({ dataSend }) => {
        setLoading(true);
        axiox.post('http://localhost:8080/answer/manager_assessment/' + year + '/' + part + '/' + userId, dataSend)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);
                alert("บันทึกข้อมูลสำเร็จ");
                setLoading(false);
            })
            .catch(err => {
                console.log(err)
                alert("บันทึกข้อมูลล้มเหลว กรุณาลองใหม่");
                setLoading(false);

            });
    }

    const renderList = () => {
        const renderItem = ({ tem, index }) => {
            return (
                <View style={{ padding: 20 }}>
                    <Text>{item.answer}</Text>
                </View>
            )
        }

        return (
            <FlatList
                data={allAnswer}
                extraData={allAnswer}
                keyExtractor={({ item, index }) => `${index}`}
                renderItem={renderItem()}
            />
        )
    }

    return (
        <View style={{ flex: 1, }}>

            {USER.position === 'ผู้จัดการ' ?

                renderList(navigation)
                :
                <View style={{ padding: 20 }}>
                    <Text style={{ color: 'blue' }}>*ให้พนักงานแสดงความคิดเห็นเชิงบวกด้วยถ้อยคำสุภาพ ว่าอยากจะปรับเปลี่ยนอะไร แก้ตรงจุดไหน</Text>
                    <Text style={{ color: 'red' }}>*ผู้จัดการหรือหัวหน้าทุกคน จะไม่เห็นว่าใครเป็นคนตอบ จะเห็นเพียงคำตอบเท่านั้น</Text>

                    <TextInput
                        style={{ backgroundColor: 'white', marginTop: 20, borderWidth: 1, padding: 10, borderRadius: 5, marginVertical: 8, height: 150 }}
                        // textContentType='telephoneNumber'
                        placeholder="คำตอบ"
                        placeholderTextColor="gray"
                        multiline
                        numberOfLines={6}
                        color='blue'
                        fontSize={16}
                        onChangeText={text => {
                            setAnswer(text);
                        }}
                        defaultValue={answer}
                    />

                    <Button
                        style={{
                            backgroundColor: 'green',
                            borderRadius: 10,
                            marginVertical: 8
                        }}
                        mode="Contained"
                        onPress={() => {
                            const dataSend = {
                                answer: answer,
                                date: moment().format('yyyy-MM-DD'),
                                department: USER.department
                            }
                            postAnswer({ dataSend });
                        }}
                    >
                        <Text style={{ alignSelf: 'center', color: 'white' }}>ส่งคำตอบ</Text>
                    </Button>
                </View>
            }
        </View>
    );
};

export default ManagerAssessmentScreen;
