import React, { useState, useEffect } from 'react';
import { View, Text, TextInput } from 'react-native';
import { Button, Card } from 'react-native-paper';

const ManagerAssessmentScreen = () => {

    const [answer, setAnswer] = useState('');

    useEffect(() => {
        setLoading(true);
        getAnswer();
    }, []);

    const getAnswer = () => {
        setLoading(true);
        axiox.get('http://localhost:8080/answer/manager_assessment/' + year + '/' + part + '/' + userId)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);
                setAnswer(res.data.answer)
                // alert("บันทึกข้อมูลสำเร็จ");
                setLoading(false);
            })
            .catch(err => {
                console.log(err)
                alert("บันทึกข้อมูลล้มเหลว กรุณาลองใหม่");
                setLoading(false);

            });
    }


    const postAnswer = () => {
        setLoading(true);
        axiox.post('http://localhost:8080/answer/manager_assessment/' + year + '/' + part + '/' + userId)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);
                alert("บันทึกข้อมูลสำเร็จ");
                setLoading(false);
            })
            .catch(err => {
                console.log(err)
                alert("บันทึกข้อมูลล้มเหลว กรุณาลองใหม่");
                setLoading(false);

            });
    }

    return (
        <View style={{ flex: 1, }}>
            <View style={{ padding: 20 }}>
                <Text style={{ color: 'blue' }}>*ให้พนักงานแสดงความคิดเห็นเชิงบวกด้วยถ้อยคำสุภาพ ว่าอยากจะปรับเปลี่ยนอะไร แก้ตรงจุดไหน</Text>
                <Text style={{ color: 'red' }}>*ผู้จัดการหรือหัวหน้าทุกคน จะไม่เห็นว่าใครเป็นคนตอบ จะเห็นเพียงคำตอบเท่านั้น</Text>

                <TextInput
                    style={{ backgroundColor: 'white', marginTop: 20, borderWidth: 1, padding: 10, borderRadius: 5, marginVertical: 8, height: 100 }}
                    // textContentType='telephoneNumber'
                    placeholder="คำตอบ"
                    placeholderTextColor="gray"
                    multiline
                    numberOfLines={6}
                    color='blue'
                    fontSize={16}
                    onChangeText={text => {
                        setAnswer(text);
                    }}
                />

                <Button
                    style={{
                        backgroundColor: 'gray',
                        borderRadius: 10,
                        marginVertical: 8
                    }}
                    mode="Contained"
                    onPress={() => {

                    }}
                >
                    <Text style={{ alignSelf: 'center', color: 'white' }}>ส่งคำตอบ</Text>
                </Button>
            </View>

        </View>
    );
};

export default ManagerAssessmentScreen;
