import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, SafeAreaView, FlatList, Alert, StyleSheet, ActivityIndicator, ScrollView } from 'react-native';
import { Button, Card } from 'react-native-paper';
// import { FlatList } from 'react-native-gesture-handler';
import Dialog from "react-native-dialog";
import axiox from 'axios';
import { COLORS } from '../constants/theme';
import { LOGIN } from '../constants/variables';
import moment from 'moment';

import AwesomeAlert from 'react-native-awesome-alerts';

const StaffQuestionnaireScreen = ({ navigation, route }) => {

    const userId = LOGIN.userid;
    // console.log(userId);
    const year = route.params.year;
    const part = route.params.part;

    var answer = {};

    const [staffAssessmentList, setStaffAssessmentList] = useState([]);

    const [isLoading, setLoading] = useState(false);
    const [alertConfirmAns, setAlertConfirmAns] = useState(false);
    // const [dialogURL, setDialogURL] = useState(false);

    // Answer
    const [staffAnswer, setStaffAnswer] = useState("");
    // Video URL
    const [isEditVideo, setIsEditVideo] = useState(false);
    const [isAnswerError, setAnserError] = useState(false);

    var staffAnswerList = [];

    const getStaffAssessmentList = () => {
        axiox.get('https://program-api.herokuapp.com/' + year + '/' + part + '/officer/' + userId)
            .then(res => {
                const data = res.data;
                console.log(data);
                setStaffAssessmentList(data);
                { setLoading(false) }
            })
            .catch(err => {
                console.log(err)
            })
    };

    const postAnswer = () => {
        console.log(answer);
        axiox.post('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.number + '/Answer/officer/' + userId, answer)
            // console.log('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.questionNumber + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log(res.data);

            })
            .catch(err => {
                console.log(err)
            });
    }

    const updateAnswer = () => {
        console.log(answer);
        axiox
            .put('https://program-api.herokuapp.com/' + year + '/' + part + '/' + answer.number + '/Answer/officer/' + userId, answer)
            .then(res => {
                console.log("response: ", res)
                // do something about response
            })
            .catch(err => {
                console.error(err)
            })
    }

    useEffect(() => {
        setLoading(true);
        getStaffAssessmentList();
    }, []);

    const renderQuestionnaireAllStaff = ({ navigation }) => {

        const renderItem = ({ item, index }) => {
            // const textInfo = "บันทึกคำตอบแล้ว\nเมื่อทำการแก้ไขให้คลิกยืนยันข้อมูลอีกครั้ง"
            // console.log(index);
            staffAnswerList[index] = item.answer;

            // textInputValue[index] = item.answer
            console.log(staffAnswerList);
            return (
                <View style={{
                    margin: 8,
                    borderWidth: 1,
                    // borderColor: 'gray',
                    borderRadius: 5,
                    elevation: 5,
                    padding: 8,
                    backgroundColor: '#fff'
                    // borderColor: COLORS.primary
                    // 
                }} >
                    <Text style={{
                        fontSize: 16,
                        fontWeight: 'bold',

                    }}>{item.number}. {item.qt}</Text>

                    {item.answer === undefined ?
                        <View>
                            <TextInput
                                style={{ borderWidth: 1, borderRadius: 5, padding: 10, marginVertical: 8, height: 100 }}
                                // textContentType='telephoneNumber'
                                placeholder="คำตอบ"
                                placeholderTextColor="gray"
                                multiline
                                numberOfLines={6}
                                color='blue'
                                fontSize={16}
                                onChangeText={text => {
                                    // setAnserError(false);
                                    setStaffAnswer(text);
                                }}
                            />
                            <Button
                                style={{
                                    backgroundColor: 'gray',
                                    borderRadius: 10,
                                    marginVertical: 8
                                }}
                                mode="Contained"
                                onPress={() => {
                                    // dialogConfirmAnswer({ index });
                                    setAlertConfirmAns(true);
                                    // confirmAlert();

                                    if (staffAnswer != "") {
                                        var answerObject = {
                                            userid: LOGIN.userid,
                                            videoURL: item.videoURL,
                                            answer: staffAnswer,
                                            number: item.number,
                                            qt: item.qt,
                                            date: moment().format('yyyy-MM-DD'),
                                            year: year
                                        };
                                        // setDialogAnswer(false);
                                        setAnserError(false);
                                        setStaffAnswer("");
                                        // setVideoURLText("");
                                        answer = answerObject;
                                        item.answer = staffAnswer;
                                        // allAnswers[index] = item.answer
                                        // console.log(answer);
                                        postAnswer();
                                    }

                                    else {
                                        setAnserError(true);
                                    }

                                    // console.log(questionAllStaffList);

                                }}>
                                <Text style={{ alignSelf: 'center', color: 'white' }}>ส่งคำตอบ</Text>

                            </Button>
                        </View>

                        :

                        <View>
                            <TextInput
                                style={{ borderWidth: 1, borderRadius: 5, padding: 10, marginVertical: 8, height: 100 }}
                                // textContentType='telephoneNumber'
                                placeholder="คำตอบ"
                                placeholderTextColor="green"
                                multiline
                                numberOfLines={6}
                                color='blue'
                                fontSize={16}
                                onChangeText={text => {
                                    setStaffAnswer(text);
                                }}
                                defaultValue={staffAnswerList[index]}
                            />
                            <Button
                                style={{
                                    backgroundColor: 'green',
                                    borderRadius: 10,
                                    marginVertical: 8
                                }}
                                mode="Contained"
                                onPress={() => {

                                    setAlertConfirmAns(true);
                                    // confirmAlert();

                                    if (staffAnswer != "") {
                                        var answerObject = {
                                            userid: LOGIN.userid,
                                            videoURL: item.videoURL,
                                            answer: staffAnswer,
                                            number: item.number,
                                            qt: item.qt,
                                            date: moment().format('yyyy-MM-DD'),
                                            year: year
                                        };
                                        // setDialogAnswer(false);
                                        setAnserError(false);
                                        // 
                                        // setVideoURLText("");
                                        answer = answerObject;
                                        item.answer = staffAnswer;
                                        updateAnswer();
                                        setStaffAnswer("");

                                        // setDialogConfirmAns(false);
                                        // setAnswer({});
                                    }
                                    else {
                                        setAnserError(true);
                                    }

                                    // console.log(questionAllStaffList);

                                }}>
                                <Text style={{ alignSelf: 'center', color: 'white' }}>ส่งคำตอบอีกครั้ง</Text>

                            </Button>
                        </View>

                    }
                </View>

            );
        }

        if (isLoading === false && staffAssessmentList.length === 0) {
            return (
                <View style={{
                    alignItems: 'center',
                    marginTop: 48
                }}>
                    <Text style={{
                        fontSize: 24,
                        color: 'red',
                        justifyContent: 'center',
                    }}>ไม่พบแบบประเมิน</Text>
                </View>
            );
        } else {
            return (

                <FlatList
                    data={staffAssessmentList}
                    extraData={staffAssessmentList}
                    keyExtractor={(item) => `${item}`}

                    renderItem={renderItem}
                    ListHeaderComponent={() => {
